<div class="py-6 px-6 lg:px-8">
    <h3 class="mb-4 text-xl font-medium text-gray-900 dark:text-white">
        <i class="bi bi-credit-card mr-2"></i>CARD PAYMENT
    </h3>
        <form class="space-y-6">
            <div>
                <label for="name" class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Name:</label>
                <input type="text" name="name" id="name" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:value-gray-400 dark:text-white" disabled="" value="Undefined">
            </div>
                <div>
                    <label for="email" class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Email:
                        
                    </label>
                    <input type="text" name="email" id="email" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:value-gray-400 dark:text-white" disabled="" value="backup@example.com">
                </div>
                    <div>
                        <label for="card" class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Card Number:</label>
                    <input type="number" name="card" id="card" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:value-gray-400 dark:text-white" disabled="" value="4215625462597845">
                </div>
                    <div class="">
                        <label for="code" class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Expiry Date:</label>
                    <input type="number" name="month" id="month" class="inline-block w-20 bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:value-gray-400 dark:text-white" disabled="" value="03">
                    <input type="number" name="year" id="year" class="inline-block w-20 ml-3 bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:value-gray-400 dark:text-white" disabled="" value="27"></div>
                    <div><label for="code" class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Security Code:</label>
                    <input type="number" name="code" id="code" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:value-gray-400 dark:text-white" disabled="" value="523"></div>
                    <p class="mb-4 text-2xl font-semibold text-lime-500 text-center">$19</p>
                    <button type="submit" class="w-full text-white bg-blue-700 hover:bg-blue-800 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700">
                        <i class="mr-2 bi bi-lock-fill"></i>PAY NOW
                </button>
                </form>
                </div>